jQuery( document ).ready( function($) {
   
	// Check if mesonary layout
	if ( $( '.dipl_instagram_feed .dipl-masonry-wrap' ).length > 0 ) {
		$( '.dipl_instagram_feed' ).each(function() {
			let $this	= $(this),
			$wrapper	= $this.find('.dipl-masonry-wrap'),
			$options	= {
				itemSelector: '.dipl_instagram_feed_item',
				layoutMode: 'masonry',
				percentPosition: true,
				resize: true,
				transitionDuration: parseInt( 3 ),
				masonry: {
					columnWidth: '.dipl_instagram_feed_item',
					gutter: '.dipl-masonry-instagram-feed-gutter'
				}
			};
		
			let $instaFeed = $wrapper.isotope($options);
			$instaFeed.imagesLoaded( function() {
				$instaFeed.isotope('layout');
				$instaFeed.isotope('reloadItems');
			} );
		} );
	}
		

} );